export interface SkeletonAvatarProps {
    /**
     * The width of the skeleton avatar, specified in pixels.
     * Determines the horizontal size of the placeholder.
     *
     * default: 48px
     *
     */
    width?: number;
    /**
     * The height of the skeleton avatar, specified in pixels.
     * Determines the vertical size of the placeholder.
     *
     * default: 48px
     */
    height?: number;
}
